package com.interview.questions;

public class Ifelse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 100;
		int b = 600;
		int c = 400;
		if(a>b & a>c) {
			System.out.println("a is big");
			
		}else if(b>c) {
			System.out.println("b is big");
		}else {
			System.out.println("c is big");
		}
			

	}

}
