package com.interview.questions;

public class LoopConcept {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 0;
		while(i<10) {
			System.out.println(i);
			i++;
			
		}
		
		
		//for loop
//		for(int j=1;j<=10;j++) {
//			System.out.println(j);
//			
//		}
		for(int k=10;k>=1;k--) {
			System.out.println(k);
		}
		

	}

}
